;Wild Commander;(C) BUDDER/MGN 2015;---------------------------------------        ORG #6011;-------WID     EQU 80AFKTIME EQU 50*60*1DEFRQ   EQU %00000001;AY+Z80CACHE   EQU %00000010CACH2   EQU %00000110INTM    EQU #5BFF;4 bytes!;-------BASIC48 EQU %00000011;-------STS     EQU #27AFBORDER  EQU #0FAFVmod    EQU #00AFVpag    EQU #01AFVpal    EQU #07AFVfm     EQU #15AFVtm     EQU #16AFVtl0    EQU #17AFVtl1    EQU #18AFVsgp    EQU #19AFVsinL   EQU #23AFVsinH   EQU #24AFPW0     EQU #10AFPW1     EQU #11AFPW2     EQU #12AFPW4     EQU #13AFSYC     EQU #20AFCYC     EQU #2BAFPE0     EQU #21AFYoffL   EQU #04AFYoffH   EQU #05AFXoffL   EQU #02AFXoffH   EQU #03AF;-------DMASL   EQU #1AAFDMASH   EQU #1BAFDMASX   EQU #1CAFDMADL   EQU #1DAFDMADH   EQU #1EAFDMADX   EQU #1FAFDMA_T   EQU #28AF;0=1, 255=256DMA_N   EQU #26AF;255=512DMA_C   EQU #27AF;RW 1 - - - 0 0 1;-------RSQB    EQU #8000CURclr  EQU %01011000;\CUR COLOR in PANzCURmsk  EQU %00000111;/MASKTBG     EQU %00010111;TEXT COLORMARKclr EQU %00010011WNDCLR  EQU %00011111GSTP    EQU 4;Step (GFX Viewer)PLHPG   EQU 96;     HEADERSPLGPG   EQU PLHPG+1; PLUGINSPLEPG   EQU PLGPG+63;/BUFPG   EQU 8;       BUFFER PageTVBPG   EQU 32;      TXTbuf Start (Page)TVEPG   EQU TVBPG+63;TXTbuf End (1024Kb)TVvPG   EQU 8;   TXT/HEX video pageRESB    EQU 29GVvPG   EQU 32;      GFX video page1GV2PG   EQU GVvPG+16;GFX video page2GV3PG   EQU GV2PG+16;GFX video page3GV4PG   EQU GV3PG+16;GFX video page4CATMX   EQU 9CTZPG   EQU 11;CATMX pages per CATPNLPG   EQU #F0;DriversINIPG   EQU PNLPG+2STRPG   EQU PNLPG+3;StreamsCPPG1   EQU 30;\CPPG2   EQU 31;/ positionsD2PG    EQU #F6D5PG    EQU #F5DBPG    EQU #F4RPPG    EQU #F7;-------RLPNL   EQU #1000RRPNL   EQU #2000RAPNL   EQU #2000-4BUX     EQU #5B00;(255)WINDC   EQU #5B02;(32)TXTBU   EQU #5B22;(64);fixed vars:PG0     EQU #6000PG4     EQU PG0+1PG8     EQU PG0+2PGC     EQU PG0+3ABT     EQU PG0+4ENT     EQU PG0+5Fun     EQU PG0+6TMN     EQU PG0+9FEP     EQU PG0+11;\XPP     EQU PG0+12;/CNFv    EQU PG0+13HEI     EQU PG0+14;\TXTmdCO EQU PG0+15;/INIflg  EQU PG0+16RSQBC   EQU PG0+17RSQB2   EQU PG0+19SDBSF   EQU PG0+21;SDdriverLOBU    EQU #B000;Main BufferETY     EQU #8000;CATZ hdrsDNT     EQU #C000;CATZCURO    EQU #C000;POZs in browsingENCODET EQU #D000;(TVvPG+1)FLG     EQU 13ELN     EQU FLG+1;---------------------------------------        DI        NOP:LD SP,#6000-2        NOP        CALL INIT,#C000        DIINTA    LD HL,I_N_T,(INTM+0),HL        LD HL,#C9FB,(INTM+2),HL        DI        LD A,INTM[,I,A:IM 2        CALL FINI,DEF        CALL PATH;-------MAIN    EI:HALT:CALL CURSOR        CALL ALT,NZ,F12:JR NZ,MAIN        CALL CTRL,NZ,F36:JR NZ,MAIN        CALL UPPP,NZ,CRUP        CALL DWWW,NZ,CRDN        CALL PGU,NZ,PGSUP        CALL PGD,NZ,PGSDN        CALL HOME,NZ,PGSHM        CALL END,NZ,PGSED        CALL LFFF,NZ,PGSHM        CALL RGGG,NZ,PGSED        CALL DELE,NZ,DelFl        CALL INS,NZ,Mark        CALL BSPC,NZ,BENT        CALL STATUS        CALL SHIFT,NZ,R2B2:JR NZ,MAIN        CALL F1,NZ,HELP        CALL F3,NZ,VIEW        CALL F10,NZ,F11MENU        CALL TABK,NZ,XPAN        CALL F4,NZ,EDIT        CALL F5,NZ,COPY        CALL F6,NZ,Move        CALL F7,NZ,MkDir        CALL F8,NZ,DelFl        CALL F9,NZ,F9STP        CALL SPKE,NZ,Mark        CALL TMAFK        CALL ENKE,NZ,ENTR        JP MAIN;-------F36     CALL F3:LD A,1:JR NZ,SBN        CALL F4:LD A,2:JR NZ,SBN        CALL F6:LD A,3:JR NZ,SBN        CALL CLAVA:JR Z,F36E        CP "r":CALL Z,SEXYF36E    LD A,1:OR A        RETSBN     CP (IX+35):JR NZ,$+3:XOR A        LD (IX+35),A        CALL SEXY        JR F36E;---------------------------------------Mark    CALL GETEN:JP Z,CRDN        EX DE,HL        LD DE,0-FLG:ADD HL,DE        LD A,(HL)        OR A:LD A,MARKclr:JR Z,$+3:XOR A        LD (HL),A        LD L,(IX+16)        LD H,(IX+17)        PUSH HL        CALL CRDN        POP HL        LD E,(IX+16)        LD D,(IX+17):OR A:SBC HL,DE        RET NZ        CALL CURSER,WLIT,CURSOR        RETIMrk    CALL GETEN:RET Z        EX DE,HL        LD DE,0-FLG:ADD HL,DE        LD A,(HL)        OR A:LD A,MARKclr:JR Z,$+3:XOR A        LD (HL),A        CALL WLIT        LD A,1,(MRCT),A:OR A        RET;---------------------------------------STATUS  XOR A:LD HL,STUS:CP (HL):RET Z        LD (HL),A        CALL ARAP        LD A,(IX+10):CP 2:RET NZ        CALL GETEN        EXA        LD HL,ENTRY,DE,LOBU,BC,8:LDIR        LD A," ",(DE),A:INC DE        LD C,3:LDIR        LD (DE),A:INC DE        EXA        LD HL,SDIR:JR Z,$+5:LD HL,SFIL        LD C,5:LDIR        LD A,32.7      LD (DE),A:INC DE        EXX        LD HL,(ARRR),DE,(ARRR+2)        PUSH IX        CALL DECZON4        POP IX        DEC DE        LD HL,DE,BC,0-9:ADD HL,BC        LD B,10,A,#30DZF     CP (HL):JR NZ,DzF        LD (HL),32:INC HL        DJNZ DZFDzF     LD HL,DE.3      INC DE        LD A,32        LD BC,3:LDDR        LD (DE),A:DEC DE        LD C,3:LDDR        LD (DE),A:DEC DE        LD C,3:LDDR        LD (DE),A:DEC DE        LD A,(HL),(DE),A        LD A,(IX+5):SUB 2_LD HL,LOBU,D,A,E,1,BC,38:CALL PRSRW        RET;-------R2B2    CALL ENKE:JP NZ,EXTZEL        LD A,1:OR A        RET;-------F12     CALL F1:JR NZ,SLM        CALL F2:JR NZ,SRM        JR BER;-------SLM     LD HL,WINDOW:JR $+5SRM     LD HL,WINDO2        CALL SXM:INC B:JR Z,BER        PUSH IX        LD IX,(IXHL),(IX+29),A,(IX+28),0        CALL DRACT,ORIGA        POP IX        CALL DRACTBER     LD A,1:OR A        RET;-------SXM     ;i:HL - SOW, IX - SOW(now);        o: A - CurPoz-1        PUSH IX        PUSH HL:CALL CURSER:POP HL        LD (IXHL),HL        PUSH HL        POP IY        LD IX,DSWND        LD A,(IY+2):ADD A,2:LD (IX+2),A        LD A,(IY+3):ADD A,2:LD (IX+3),A        LD A,(IY+29):INC A:LD (IX+12),A        CALL PRWOWSMC     EI:HALT:CALL CURSOR        CALL UPPP,NZ,CrUP        CALL DWWW,NZ,CrDN        CALL ESC:LD B,#FF:JR NZ,AMC        CALL ENKE:JR Z,SMC        LD B,0AMC     PUSH BC        CALL RRESB        POP BC        LD A,(IX+12):DEC A        POP IX        RET;---------------------------------------ENTR    CALL GETEN        EXA:LD A,H:OR L:RET Z        EXA:JP NZ,VIE        INC DE        CALL MPATH        CALL CURSER        JP FOLDERBENT    LD HL,0:CALL GETen:RET NZ        LD A,H:OR L:RET Z        LD B,2Bent    INC DE:LD A,(DE):CP ".":RET NZ        DJNZ Bent        INC DE:LD A,(DE):OR A:RET NZ.2      DEC DE        CALL MPATH        CALL CURSER        XOR A        LD (IX+12),1        LD (IX+16),A        LD (IX+17),A        JP FOLDER;---------------------------------------XPAN    PUSH IX        POP HL        LD DE,WINDO2:OR A:SBC HL,DE        JR Z,LPAN;-------RPAN    CALL CURSER        CALL MNGP        LD A,#42,(RAPNL+#C000),A        LD IX,WINDO2:CALL DRACT        JP STTLPAN    CALL CURSER        CALL MNGP        LD A,#41,(RAPNL+#C000),A        LD IX,WINDOW:CALL DRACT        JP STT;---------------------------------------PGSED   CALL CURSER        LD A,(IX+20):OR A:JR NZ,CR4        LD L,(IX+16),H,(IX+17)EDD     PUSH HL        CALL Gwe        POP HL:INC HL:JR NZ,EDD        LD A,(IX+5):SUB 1,(IX+10)        LD B,0,C,A        OR A:SBC HL,BC        LD A,H:CP #FF:RET NC        LD (IX+16),L,(IX+17),H        CALL WLIT        JR CR4PGSDN   CALL CURSER        LD A,(IX+20):OR A:JR NZ,CR4        LD A,(IX+5):SUB 2,(IX+10)        LD C,A,B,0        LD L,(IX+16),H,(IX+17):ADD HL,BC        LD (IX+16),L,(IX+17),H        CALL WLITCR4     CALL CR4EG        JP STTSM      PUSH HL:CALL CURSER:POP HL        JR SLPGSUP   ;LD A,(IX+21):OR A:RET NZ        LD A,(IX+5):SUB 2,(IX+10)        LD C,A,B,0        LD L,(IX+16),H,(IX+17)        OR A:SBC HL,BC:JR NC,SMPGSHM   CALL CURSER        LD HL,0,(IX+12),1        JR SL;-------CRDN    LD A,(IX+12)        CP (IX+13):JR NC,SRLDNRDN     CALL CURSER        INC (IX+12)        JR STTSRLDN   LD A,(IX+20):OR A:RET NZ        LD L,(IX+16),H,(IX+17):INC HLSL      LD (IX+16),L,(IX+17),H        CALL WLIT        CALL CURSOR        RETCR4EG   LD A,(IX+20):OR A:RET Z        LD A,(IX+13):OR A:RET Z        LD (IX+12),A        RETCR4eg   LD A,(IX+13)        OR A:JR NZ,$+3:INC A        CP (IX+12):RET NC        LD (IX+12),A        RET;-------CRUP    LD A,(IX+12)        CP 1:JR Z,SRLUPRUP     CALL CURSER        DEC (IX+12)STT     CALL CURSOR        LD A,1,(STUS),A        RETSRLUP   LD A,(IX+21):OR A:RET NZ        LD L,(IX+16),H,(IX+17):DEC HL        JR SL;-------CrDN    LD A,(IX+12)        CP (IX+13):RET NC        JR RDNCrUP    LD A,(IX+12)        CP 1:RET Z        JR RUP;-------CrPU    CALL CURSER:LD (IX+12),1:JR STTCrPD    CALL CURSER        LD A,(IX+13),(IX+12),A:JR STT;-------PANK    LD L,(IX+18)        LD H,(IX+19)        LD A,(IX+17):CP H:RET C        LD A,(IX+16):CP L:RET C        LD A,(IX+5):SUB 2,(IX+10)        LD B,0,C,A        OR A:SBC HL,BC:JR NC,$+5:LD HL,0        LD (IX+16),L        LD (IX+17),H        RET;---------------------------------------PANE    CALL PANELI        LD (IX+23),A,(IX+24),A        LD (IX+12),1        LD (IX+16),A,(IX+17),A,(IX+26),A        RETPANELI  ;i:IX - SOW        LD A,1        LD (STUS),A        XOR A        LD (IX+13),A        LD (IX+15),A        LD (IX+20),A        LD (IX+21),A        RETGETEN   ;i:IX - SOW;        o:DE - flag addres;          HL - ENTRY addres;          ENTRY(12), BRRR(8)        LD B,0,C,(IX+12):DEC C        LD L,(IX+16),H,(IX+17)        ADD HL,BCGETen   CALL GWE        PUSH HL        INC HL        LD DE,BRRR,BC,8:LDIR        LD C,FLG-9:ADD HL,BC        LD DE,ENTRY:CALL LNGSNM        XOR A:LD (DE),A        POP HL        PUSH HL        LD C,FLG:ADD HL,BC:EX DE,HL        POP HL        LD A,(DE):OR A        RET;-------GETWE   LD L,(IX+16),H,(IX+17)GWE     ADD HL,HL,HL,HL        LD BC,DNT        ADD HL,BC        CALL CAT2        LD A,(HL):INC L:LD (IX+26),A        LD C,(HL):INC L        LD B,(HL):INC L        LD A,(HL),H,B,L,C        LD C,A        JP CAT1Gwe     CALL GWE:LD A,H:OR L:RET;-------CURPOZ  LD A,(IX+22):OR A:RET Z        CALL MNGC        LD DE,CURO        LD C,(IX+16),B,(IX+17)        LD L,(IX+23),H,(IX+24)        LD A,(IX+12)        DEC A:JR Z,LUPLup     LD A,(IX+12)        ADD HL,DE        LD (HL),C:INC HL        LD (HL),B:INC HL        LD (HL),A:INC HL        LD (HL),0:INC HL        LD (IX+12),1        LD (IX+16),0        LD (IX+17),0NEKO    OR A:SBC HL,DE        LD (IX+23),L,(IX+24),H        RET;-------LUP     LD A,B:OR C:JR NZ,Lup        LD A,L:OR H:JR Z,Lup        ADD HL,DE        DEC HL        DEC HL:LD A,(HL),(IX+12),A        DEC HL:LD A,(HL),(IX+17),A        DEC HL:LD A,(HL),(IX+16),A        JR NEKO;---------------------------------------ER0     LD HL,TER0ERX     CALL ERR0        PUSH IX        POP HL        CALL SXM        LD (IX+29),A,(IX+28),0        CALL DRACT        JR ORIGER1     LD HL,TER1        JR ERX;-------ORIGA2  LD A,(HEI):SUB 2:LD (IX+5),A        CALL PRWOWORIG    CALL IDE_INI        XOR A:CALL SEL_DEV:JR NZ,ER0        CALL HDD:JR NZ,ER1        LD HL,BRRR,B,4:CALL NOPING        JP PANEORIGA   CALL ORIGA2FOLD    CALL NEWDIR        CALL PANK        CALL WLIT,CR4eg        XOR A        RETFOLDER  CALL PANELI,CURPOZ        CALL FOLD        RET;---------------------------------------;GENERATING DIRECTORY IN RAM:NEWDIR  ;i:BRRR(4) - fstCLUS of NEW DIR        CALL PPATH        LD HL,BRRR:CALL GLSTCAT        XOR A        LD (IX+18),A,(IX+19),A        INC A:LD (IX+26),A:CALL CAT1        CALL CAT0        XOR A:LD (DNT+ELN),A        LD DE,DNT        PUSH DE        XOR A:LD (DE),A:INC DE        CALL BDSL,NXTETY,BDSH        LD BC,DE,HL,ETY-1        POP DE:JP Z,BEX        LD HL,FLG:ADD HL,DE        LD A,(HL):OR A:JR NZ,XX        INC HL:LD A,(HL):CP ".":JR NZ,XX        INC HL:LD A,(HL):OR AXX      LD HL,ETY,DE,DNT:JR Z,BUMM        LD A,(IX+26)        LD (HL),A:INC L        LD (HL),E:INC L        LD (HL),D:INC L        LD A,(TTHL)        LD (HL),A:INC HL        LD DE,BC:INC (IX+18)BUMM    DEC HLBUM     LD A,D:CP #FE:JR C,DOOM        INC (IX+26):CALL CAT1        LD DE,DNTDOOM    LD A,(IX+26):CP CATMX:JR NC,BEX        LD C,A        LD A,H:CP ETY[:JR C,DuuM+1        LD A,(TTHL)DuuM    LD (HL),A:INC HL        LD (HL),C:INC L        LD (HL),E:INC L        LD (HL),D:INC L        INC (IX+18):JP NZ,AXA        INC (IX+19)AXA     XOR A:LD (DE),A:INC DE        PUSH HL:CALL BDSL,NXTETY,BDSH        POP HL:JR NZ,BUM.4      DEC HL        DEC (IX+18)        LD A,(IX+18):INC A:JR NZ,BEX        DEC (IX+19)BEX     XOR A        INC HL        LD (HL),A:INC L        LD (HL),A:INC L        LD (HL),A        LD A,2:CALL MNG8        LD A,(IX+26):CP 1:RET NZ        LD A,(IX+19):OR A:JR NZ,SPM        LD A,(IX+18):CP 200:JR C,SORTZSPM     PUSH IX:LD IX,SMWND:CALL PRWOW        POP IX        CALL SORTZ        PUSH IX:LD IX,SMWND:CALL RRESB        POP IX        RETSORTZ   LD A,CACH2:CALL CaC        CALL SORT        CALL FRST        JP CACLNGSNM  LD C,(HL):INC HL        LD B,8LNG_    LD A,(HL)        OR A:JR Z,LNGSF        CP ".":JR Z,LNGSE        LD (DE),A:INC HL,DE        DJNZ LNG_        LD A,(HL):OR A:JR Z,LNGSF        LD A,C:OR A:JR Z,LNGSFSE      XOR A:LD BC,256:CPIR:DEC HL        LD A,256-1:SUB C:JR Z,LNGSF        CP 4:LD B,A:JR C,Se        LD B,4Se      DEC HL:LD A,(HL):CP ".":JR Z,SFE        DJNZ SeLNGSF   INC B,B,BSF      LD A," ",(DE),A:INC DE:DJNZ $-2        RETSFE     LD B,3Sf      INC HL:LD A,(HL):OR A:JR Z,SF        CP #40:JR C,EXTR        CP #80:JR NC,$+4:RES 5,AEXTR    LD (DE),A:INC DE        DJNZ Sf    