ADD HL,BC        CP 2:JR NZ,RAT        LD BC,0-(512+197):ADD HL,BC        LD DE,LOBU,BC,256:LDIR        CALL AJTCAF     LD HL,0        JR MAF;-------TMAFK   CALL TAB4E:JR NZ,CAF        LD HL,(TABTM):INC HL        LD DE,(AFTM)        OR A:SBC HL,DE:JR NC,RATM        ADD HL,DEMAF     LD (TABTM),HL        RET;-------P8000   PUSH AF:XOR A:CALL MNGC        POP AF        LD BC,(PG0),(FEP),BC        PUSH BC:LD BC,(BRRR+2)        PUSH BC:LD BC,(BRRR)        PUSH BC        LD BC,(RSQBC),(RSQB2),BC        LD (IXBU),IX        CALL #8000        LD IX,(IXBU)        LD HL,(RSQB2),(RSQBC),HL        POP HL:LD (BRRR),HL        POP HL:LD (BRRR+2),HL        POP HL        PUSH AF        LD A,L:CALL MNG0,DEF        CALL INTPLD        POP AF        RET;---------------------------------------        INCL "PS2P"        INCL "WCIF"        INCL "WCVLS"        INCL "WCFX"        INCL "WCVW"        INCL "WCINI";-------DEHR1   INCB "DEHR1M"MLZ4    INCB "MLZ40"GIG_PAL INCB "GIG_PAL"FONT    INCB "FONT32L"NOre    ORG ENCODET,$ENCOD   INCL "ENCODET"KOIWIN  INCB "KOI_WIN"ENCND;---------------------------------------        ORG #C000CORE    EQU #4000IDE_INI EQU CORESEL_DEV EQU CORE+3REINI   EQU CORE+6HDD     EQU CORE+9RDD     EQU CORE+12SDD     EQU CORE+15GIPAG   EQU CORE+18LOAD512 EQU CORE+21SAVE512 EQU CORE+24DOS_SWP EQU CORE+27GLSTCAT EQU CORE+30TLSTCAT EQU CORE+33SRHFCL  EQU CORE+36;EQU CORE+39MKSG    EQU CORE+42;EQU CORE+45RFRH    EQU CORE+48GENTRY  EQU CORE+51TENTRY  EQU CORE+54MKFILE  EQU CORE+57MKDIR   EQU CORE+60DELFL   EQU CORE+63RENAM   EQU CORE+66DLSG    EQU CORE+69CHTOSE  EQU CORE+72;EQU CORE+75SRHDRN  EQU CORE+78LOAD256 EQU CORE+81LOADNON EQU CORE+84NXTETY  EQU CORE+87NXTETY2 EQU CORE+90;-------        INCB "WDOSFC33"